	private static  JMenuItem New = new JMenuItem("New");
	private static JMenuItem Open = new JMenuItem("Open");
	private static JMenuItem SaveAs = new JMenuItem("Save As");
	private static JMenuItem Exit = new JMenuItem("Exit");

